import React, { useState } from 'react';
import { AlertCircle, CheckCircle, Loader, FileText, Sparkles, Shield, AlertTriangle, BarChart3 } from 'lucide-react';

export default function FakeNewsDetector() {
  const [text, setText] = useState('');
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const API_URL = 'http://localhost:5000/predict';

  const analyzeNews = async () => {
    if (!text.trim() || text.length < 10) {
      setError('Please enter at least 10 characters');
      return;
    }

    setLoading(true);
    setError('');
    setResult(null);

    try {
      const response = await fetch(API_URL, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ text }),
      });

      const data = await response.json();

      if (response.ok) {
        setResult(data);
      } else {
        setError(data.error || 'Failed to analyze text');
      }
    } catch (err) {
      setError('Could not connect to the server. Make sure Flask API is running on http://localhost:5000');
    } finally {
      setLoading(false);
    }
  };

  const clearAll = () => {
    setText('');
    setResult(null);
    setError('');
  };

  const samples = [
    { text: "Scientists at MIT have discovered a new renewable energy source that could power entire cities.", icon: "ðŸ”¬" },
    { text: "You won't believe what this celebrity did! Doctors hate this one weird trick!", icon: "âš ï¸" },
    { text: "The government announced new infrastructure spending plans in today's budget proposal.", icon: "ðŸ›ï¸" }
  ];

  const loadSample = (sample) => {
    setText(sample);
    setResult(null);
    setError('');
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-50 to-white p-4 sm:p-6 md:p-8 lg:p-12 relative overflow-hidden">
      {/* Subtle Animated Background Elements */}
      <div className="absolute inset-0 overflow-hidden pointer-events-none">
        <div className="absolute top-20 left-10 w-48 h-48 md:w-64 md:h-64 bg-blue-100 rounded-full mix-blend-multiply filter blur-2xl opacity-10 animate-blob-slow"></div>
        <div className="absolute top-40 right-10 w-48 h-48 md:w-64 md:h-64 bg-indigo-100 rounded-full mix-blend-multiply filter blur-2xl opacity-10 animate-blob-slow animation-delay-2000"></div>
        <div className="absolute bottom-20 left-1/2 w-48 h-48 md:w-64 md:h-64 bg-purple-100 rounded-full mix-blend-multiply filter blur-2xl opacity-10 animate-blob-slow animation-delay-4000"></div>
      </div>

      <div className="max-w-7xl mx-auto relative z-10">
        {/* Header with Subtle Animation */}
        <div className="text-center mb-8 md:mb-12 animate-fade-in">
          <div className="flex justify-center items-center gap-3 md:gap-4 mb-3 md:mb-4">
            <div className="relative">
              <div className="absolute inset-0 bg-gradient-to-r from-blue-200 to-indigo-300 rounded-full blur-sm opacity-40"></div>
              <div className="relative bg-white p-3 md:p-4 rounded-full shadow-md">
                <Shield className="w-8 h-8 md:w-12 md:h-12 text-blue-600" />
              </div>
            </div>
          </div>
          <h1 className="text-4xl sm:text-5xl md:text-6xl lg:text-7xl font-bold mb-3 md:mb-4 text-gray-900">
            Fake News Detector
          </h1>
          <p className="text-gray-600 text-base sm:text-lg md:text-xl font-medium flex items-center justify-center gap-2">
            AI-Powered Truth Verification System
          </p>
          <div className="mt-3 md:mt-4 inline-flex items-center gap-2 bg-white/80 px-3 py-1 md:px-4 md:py-2 rounded-full shadow-sm text-xs sm:text-sm font-semibold text-gray-700">
            Powered by Machine Learning
          </div>
        </div>

        {/* Main Card with Clean Design */}
        <div className="bg-white rounded-2xl shadow-lg p-6 sm:p-8 md:p-10 mb-8 border border-gray-100">
          {/* Stats Bar - Responsive Grid */}
          <div className="grid grid-cols-1 sm:grid-cols-3 gap-4 mb-8">
            <div className="bg-blue-50 p-4 rounded-lg text-center border border-blue-100">
              <BarChart3 className="w-5 h-5 md:w-6 md:h-6 text-blue-600 mx-auto mb-2" />
              <div className="text-xl md:text-2xl font-bold text-blue-900">95%+</div>
              <div className="text-xs text-blue-700 font-medium">Accuracy</div>
            </div>
            <div className="bg-indigo-50 p-4 rounded-lg text-center border border-indigo-100">
              <Sparkles className="w-5 h-5 md:w-6 md:h-6 text-indigo-600 mx-auto mb-2" />
              <div className="text-xl md:text-2xl font-bold text-indigo-900">AI</div>
              <div className="text-xs text-indigo-700 font-medium">Powered</div>
            </div>
            <div className="bg-purple-50 p-4 rounded-lg text-center border border-purple-100">
              <Shield className="w-5 h-5 md:w-6 md:h-6 text-purple-600 mx-auto mb-2" />
              <div className="text-xl md:text-2xl font-bold text-purple-900">Real-time</div>
              <div className="text-xs text-purple-700 font-medium">Analysis</div>
            </div>
          </div>

          {/* Input Section - Responsive Height */}
          <div className="mb-6">
            <label className="block text-sm font-semibold text-gray-800 mb-3 flex items-center gap-2">
              <FileText className="w-4 h-4 md:w-5 md:h-5 text-blue-600" />
              Enter News Article or Headline
            </label>
            <div className="relative">
              <textarea
                value={text}
                onChange={(e) => setText(e.target.value)}
                placeholder="Paste your news article, headline, or any text you want to verify..."
                className="w-full h-40 sm:h-48 md:h-52 p-4 md:p-5 border border-gray-200 rounded-xl focus:border-blue-500 focus:ring-2 focus:ring-blue-200 focus:outline-none resize-none text-gray-700 transition-all shadow-sm bg-white font-medium text-sm md:text-base"
              />
              <div className="absolute bottom-3 right-3 md:bottom-4 md:right-4 bg-white px-2 py-1 md:px-3 md:py-1 rounded-full shadow-sm">
                <span className={`text-xs md:text-sm font-semibold ${text.length >= 10 ? 'text-green-600' : 'text-gray-400'}`}>
                  {text.length} chars
                </span>
              </div>
            </div>
            {text.length > 0 && (
              <button
                onClick={clearAll}
                className="mt-3 text-xs md:text-sm text-red-500 hover:text-red-700 font-semibold flex items-center gap-1 transition-colors"
              >
                <span>âœ•</span> Clear Text
              </button>
            )}
          </div>

          {/* Action Button - Responsive */}
          <button
            onClick={analyzeNews}
            disabled={loading || text.length < 10}
            className={`w-full py-4 md:py-5 rounded-xl font-semibold text-white text-base md:text-lg transition-all transform hover:scale-[1.01] active:scale-99 shadow-md relative overflow-hidden ${
              loading || text.length < 10
                ? 'bg-gray-300 cursor-not-allowed'
                : 'bg-gradient-to-r from-blue-600 to-indigo-600 hover:shadow-xl'
            }`}
          >
            {loading ? (
              <span className="flex items-center justify-center gap-2 md:gap-3">
                <Loader className="w-5 h-5 md:w-6 md:h-6 animate-spin" />
                Analyzing...
              </span>
            ) : (
              <span className="flex items-center justify-center gap-2">
                Analyze News Article
              </span>
            )}
          </button>

          {/* Error Message - Responsive */}
          {error && (
            <div className="mt-6 p-4 md:p-5 bg-red-50 border border-red-200 rounded-xl flex items-start gap-3 shadow-sm">
              <AlertCircle className="w-5 h-5 md:w-6 md:h-6 text-red-500 mt-0.5 flex-shrink-0" />
              <div>
                <p className="text-red-900 font-semibold text-base md:text-lg">Error</p>
                <p className="text-red-700 text-xs md:text-sm mt-1">{error}</p>
              </div>
            </div>
          )}

          {/* Results with Smooth Animation */}
          {result && (
            <div className="mt-8 space-y-5 animate-slide-up">
              <div
                className={`p-6 md:p-8 rounded-2xl border shadow-xl relative overflow-hidden ${
                  result.prediction === 'REAL'
                    ? 'bg-green-50 border-green-200'
                    : 'bg-red-50 border-red-200'
                }`}
              >
                <div className="relative z-10">
                  <div className="flex items-center gap-3 md:gap-4 mb-6">
                    <div className={`p-3 md:p-4 rounded-xl ${
                      result.prediction === 'REAL' 
                        ? 'bg-green-100 shadow-md' 
                        : 'bg-red-100 shadow-md'
                    }`}>
                      {result.prediction === 'REAL' ? (
                        <CheckCircle className="w-8 h-8 md:w-10 md:h-10 text-green-600" />
                      ) : (
                        <AlertTriangle className="w-8 h-8 md:w-10 md:h-10 text-red-600" />
                      )}
                    </div>
                    <div>
                      <h3 className="text-3xl md:text-4xl font-bold text-gray-900">
                        {result.prediction === 'REAL' ? 'Real News' : 'Fake News'}
                      </h3>
                      <p className={`text-sm font-semibold mt-1 ${
                        result.prediction === 'REAL' ? 'text-green-600' : 'text-red-600'
                      }`}>
                        AI Verification Complete
                      </p>
                    </div>
                  </div>

                  {/* Confidence Bars - Clean and Responsive */}
                  <div className="space-y-4 mb-6">
                    <div className="bg-white p-4 md:p-5 rounded-xl shadow-sm">
                      <div className="flex justify-between mb-2">
                        <span className="text-sm font-semibold text-gray-800 flex items-center gap-2">
                          <CheckCircle className="w-4 h-4 text-green-600" />
                          Real News Probability
                        </span>
                        <span className="text-base md:text-lg font-bold text-green-700">
                          {result.confidence_percentage.real}
                        </span>
                      </div>
                      <div className="w-full bg-gray-100 rounded-full h-2 md:h-3">
                        <div
                          className="bg-green-500 h-2 md:h-3 rounded-full transition-all duration-500"
                          style={{ width: result.confidence_percentage.real }}
                        />
                      </div>
                    </div>

                    <div className="bg-white p-4 md:p-5 rounded-xl shadow-sm">
                      <div className="flex justify-between mb-2">
                        <span className="text-sm font-semibold text-gray-800 flex items-center gap-2">
                          <AlertTriangle className="w-4 h-4 text-red-600" />
                          Fake News Probability
                        </span>
                        <span className="text-base md:text-lg font-bold text-red-700">
                          {result.confidence_percentage.fake}
                        </span>
                      </div>
                      <div className="w-full bg-gray-100 rounded-full h-2 md:h-3">
                        <div
                          className="bg-red-500 h-2 md:h-3 rounded-full transition-all duration-500"
                          style={{ width: result.confidence_percentage.fake }}
                        />
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>

        {/* Sample Texts - Responsive Grid */}
        <div className="bg-white rounded-2xl shadow-lg p-6 sm:p-8 border border-gray-100">
          <h3 className="text-xl md:text-2xl font-semibold text-gray-800 mb-6 flex items-center gap-3">
            Try Sample Articles
          </h3>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            {samples.map((sample, index) => (
              <button
                key={index}
                onClick={() => loadSample(sample.text)}
                className="text-left p-5 md:p-6 bg-white hover:bg-blue-50 border border-gray-200 hover:border-blue-300 rounded-xl transition-all shadow-sm hover:shadow-md"
              >
                <div className="text-2xl md:text-3xl mb-3">{sample.icon}</div>
                <p className="text-sm text-gray-700 font-medium leading-relaxed">{sample.text}</p>
              </button>
            ))}
          </div>
        </div>

        {/* Footer - Minimal */}
        <div className="text-center mt-10 text-gray-600 font-medium text-sm">
          Built with React, Machine Learning & NLP
        </div>
      </div>

      <style>{`
        @keyframes blob-slow {
          0%, 100% { transform: translate(0, 0) scale(1); }
          33% { transform: translate(20px, -30px) scale(1.05); }
          66% { transform: translate(-15px, 15px) scale(0.95); }
        }

        @keyframes slide-up {
          from {
            opacity: 0;
            transform: translateY(20px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }

        @keyframes fade-in {
          from { opacity: 0; transform: translateY(-10px); }
          to { opacity: 1; transform: translateY(0); }
        }

        .animate-blob-slow {
          animation: blob-slow 10s infinite;
        }

        .animation-delay-2000 {
          animation-delay: 2s;
        }

        .animation-delay-4000 {
          animation-delay: 4s;
        }

        .animate-slide-up {
          animation: slide-up 0.5s ease-out;
        }

        .animate-fade-in {
          animation: fade-in 0.6s ease-out;
        }
      `}</style>
    </div>
  );
}
