import React, { useState } from "react";
import {
  Loader,
  ShieldCheck,
  AlertTriangle,
  FileText,
  Sparkles,
  CheckCircle,
} from "lucide-react";

export default function FakeNewsDetector() {
  const [text, setText] = useState("");
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const API_URL = "http://localhost:5000/predict";

  const analyzeNews = async () => {
    if (text.length < 10) {
      setError("Please enter at least 10 characters.");
      return;
    }

    setLoading(true);
    setError("");
    setResult(null);

    try {
      const response = await fetch(API_URL, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ text }),
      });

      const data = await response.json();

      if (response.ok) {
        setResult(data);
      } else {
        setError(data.error || "Failed to analyze text.");
      }
    } catch (err) {
      setError("Cannot connect to the server. Is Flask running?");
    } finally {
      setLoading(false);
    }
  };

  const samples = [
    {
      text: "Scientists at MIT created a new renewable energy source.",
      icon: "ðŸ”¬",
    },
    {
      text: "You won't believe what this celebrity did!",
      icon: "âš ï¸",
    },
    {
      text: "Government announces new budget plans.",
      icon: "ðŸ›ï¸",
    },
  ];

  return (
    <div className="min-h-screen bg-gray-100 px-4 py-10">
      {/* HEADER */}
      <div className="max-w-3xl mx-auto text-center mb-10">
        <div className="flex justify-center mb-3">
          <ShieldCheck className="w-12 h-12 text-indigo-600" />
        </div>
        <h1 className="text-4xl font-bold text-gray-900">
          Fake News Detector
        </h1>
        <p className="text-gray-600 mt-2 flex items-center justify-center gap-1">
          <Sparkles className="w-4 h-4 text-indigo-500" />
          AI-powered verification system
          <Sparkles className="w-4 h-4 text-indigo-500" />
        </p>
      </div>

      {/* MAIN CARD */}
      <div className="max-w-3xl mx-auto bg-white shadow-md rounded-xl p-6">
        {/* INPUT */}
        <label className="text-sm font-semibold flex items-center gap-2 mb-2">
          <FileText className="w-4 h-4 text-indigo-600" />
          Enter your text
        </label>

        <textarea
          value={text}
          onChange={(e) => setText(e.target.value)}
          className="w-full h-40 p-4 border rounded-lg focus:ring-2 focus:ring-indigo-300 outline-none"
          placeholder="Paste article or headline here..."
        />

        {/* BUTTON */}
        <button
          onClick={analyzeNews}
          disabled={loading || text.length < 10}
          className={`w-full mt-4 py-3 rounded-lg text-white font-semibold transition ${
            loading || text.length < 10
              ? "bg-gray-300 cursor-not-allowed"
              : "bg-indigo-600 hover:bg-indigo-700"
          }`}
        >
          {loading ? (
            <span className="flex justify-center gap-2">
              <Loader className="w-5 h-5 animate-spin" />
              Analyzing...
            </span>
          ) : (
            "Analyze"
          )}
        </button>

        {/* ERROR */}
        {error && (
          <div className="mt-4 p-3 bg-red-50 border border-red-300 rounded-lg text-red-700 flex items-center gap-2">
            <AlertTriangle className="w-5 h-5" />
            {error}
          </div>
        )}

        {/* RESULT */}
        {result && (
          <div
            className={`mt-6 p-5 rounded-lg border ${
              result.prediction === "REAL"
                ? "bg-green-50 border-green-400"
                : "bg-red-50 border-red-400"
            }`}
          >
            <div className="flex items-center gap-3 mb-3">
              {result.prediction === "REAL" ? (
                <CheckCircle className="w-7 h-7 text-green-600" />
              ) : (
                <AlertTriangle className="w-7 h-7 text-red-600" />
              )}
              <h3 className="text-2xl font-bold text-gray-900">
                {result.prediction === "REAL" ? "Real News" : "Fake News"}
              </h3>
            </div>

            <p className="text-gray-700 font-medium">
              Real Probability: {result.confidence_percentage.real}
            </p>
            <p className="text-gray-700 font-medium">
              Fake Probability: {result.confidence_percentage.fake}
            </p>
          </div>
        )}
      </div>

      {/* SAMPLE TEXTS */}
      <div className="max-w-3xl mx-auto mt-10">
        <h2 className="text-xl font-semibold mb-3 text-gray-800">
          Try a sample
        </h2>
        <div className="grid md:grid-cols-3 gap-4">
          {samples.map((s, i) => (
            <button
              key={i}
              onClick={() => setText(s.text)}
              className="p-4 bg-white border rounded-lg shadow-sm hover:shadow-md transition text-left"
            >
              <div className="text-2xl mb-2">{s.icon}</div>
              <p className="text-sm text-gray-700">{s.text}</p>
            </button>
          ))}
        </div>
      </div>
    </div>
  );
}
