import React, { useState } from 'react';
import { AlertCircle, CheckCircle, Loader, FileText, Sparkles, Shield, AlertTriangle, BarChart3, Zap } from 'lucide-react';

export default function FakeNewsDetector() {
  const [text, setText] = useState('');
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const API_URL = 'http://localhost:5000/predict';

  const analyzeNews = async () => {
    if (!text.trim() || text.length < 10) {
      setError('Please enter at least 10 characters');
      return;
    }

    setLoading(true);
    setError('');
    setResult(null);

    try {
      const response = await fetch(API_URL, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ text }),
      });

      const data = await response.json();

      if (response.ok) {
        setResult(data);
      } else {
        setError(data.error || 'Failed to analyze text');
      }
    } catch (err) {
      setError('Could not connect to the server. Make sure Flask API is running on http://localhost:5000');
    } finally {
      setLoading(false);
    }
  };

  const clearAll = () => {
    setText('');
    setResult(null);
    setError('');
  };

  const samples = [
    { text: "Scientists at MIT have discovered a new renewable energy source that could power entire cities.", icon: "ðŸ”¬", type: "real" },
    { text: "You won't believe what this celebrity did! Doctors hate this one weird trick!", icon: "âš ï¸", type: "fake" },
    { text: "The government announced new infrastructure spending plans in today's budget proposal.", icon: "ðŸ›ï¸", type: "real" }
  ];

  const loadSample = (sample) => {
    setText(sample);
    setResult(null);
    setError('');
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-50 via-purple-50 to-blue-50 p-4 md:p-8 relative overflow-hidden">
      {/* Animated Background Elements */}
      <div className="absolute inset-0 overflow-hidden pointer-events-none">
        <div className="absolute top-20 left-10 w-72 h-72 bg-indigo-200 rounded-full mix-blend-multiply filter blur-xl opacity-30 animate-blob"></div>
        <div className="absolute top-40 right-10 w-72 h-72 bg-purple-200 rounded-full mix-blend-multiply filter blur-xl opacity-30 animate-blob animation-delay-2000"></div>
        <div className="absolute bottom-20 left-1/2 w-72 h-72 bg-blue-200 rounded-full mix-blend-multiply filter blur-xl opacity-30 animate-blob animation-delay-4000"></div>
      </div>

      <div className="max-w-6xl mx-auto relative z-10">
        {/* Header with Animation */}
        <div className="text-center mb-12 animate-fade-in">
          <div className="flex justify-center items-center gap-4 mb-4">
            <div className="relative">
              <div className="absolute inset-0 bg-gradient-to-r from-indigo-400 to-purple-500 rounded-full blur-lg opacity-50 animate-pulse"></div>
              <div className="relative bg-white p-4 rounded-full shadow-xl">
                <Shield className="w-12 h-12 text-transparent bg-clip-text bg-gradient-to-r from-indigo-600 to-purple-600" style={{ fill: 'url(#gradient)' }} />
                <svg width="0" height="0">
                  <defs>
                    <linearGradient id="gradient" x1="0%" y1="0%" x2="100%" y2="100%">
                      <stop offset="0%" stopColor="#4F46E5" />
                      <stop offset="100%" stopColor="#9333EA" />
                    </linearGradient>
                  </defs>
                </svg>
              </div>
            </div>
          </div>
          <h1 className="text-5xl md:text-7xl font-extrabold mb-4 bg-gradient-to-r from-indigo-600 via-purple-600 to-blue-600 bg-clip-text text-transparent">
            Fake News Detector
          </h1>
          <p className="text-gray-600 text-xl font-medium flex items-center justify-center gap-2">
            <Sparkles className="w-5 h-5 text-purple-500" />
            AI-Powered Truth Verification System
            <Sparkles className="w-5 h-5 text-purple-500" />
          </p>
          <div className="mt-4 inline-flex items-center gap-2 bg-white/70 backdrop-blur-sm px-4 py-2 rounded-full shadow-lg">
            <div className="w-2 h-2 bg-green-500 rounded-full animate-pulse"></div>
            <span className="text-sm font-semibold text-gray-700">98.79% Accuracy</span>
            <span className="text-gray-400">â€¢</span>
            <span className="text-sm text-gray-600">ML Powered</span>
          </div>
        </div>

        {/* Main Card with Glass Effect */}
        <div className="bg-white/80 backdrop-blur-md rounded-3xl shadow-2xl p-8 md:p-10 mb-8 border border-white/50">
          {/* Stats Bar */}
          <div className="grid grid-cols-3 gap-4 mb-8">
            <div className="bg-gradient-to-br from-indigo-50 to-indigo-100 p-4 rounded-xl text-center border border-indigo-200">
              <BarChart3 className="w-6 h-6 text-indigo-600 mx-auto mb-2" />
              <div className="text-2xl font-bold text-indigo-900">98.79%</div>
              <div className="text-xs text-indigo-700 font-medium">Accuracy</div>
            </div>
            <div className="bg-gradient-to-br from-purple-50 to-purple-100 p-4 rounded-xl text-center border border-purple-200">
              <Zap className="w-6 h-6 text-purple-600 mx-auto mb-2" />
              <div className="text-2xl font-bold text-purple-900">Instant</div>
              <div className="text-xs text-purple-700 font-medium">Analysis</div>
            </div>
            <div className="bg-gradient-to-br from-blue-50 to-blue-100 p-4 rounded-xl text-center border border-blue-200">
              <Shield className="w-6 h-6 text-blue-600 mx-auto mb-2" />
              <div className="text-2xl font-bold text-blue-900">Secure</div>
              <div className="text-xs text-blue-700 font-medium">Verified</div>
            </div>
          </div>

          {/* Input Section */}
          <div className="mb-6">
            <label className="block text-sm font-bold text-gray-800 mb-3 flex items-center gap-2">
              <FileText className="w-5 h-5 text-indigo-600" />
              Enter News Article or Headline
            </label>
            <div className="relative">
              <textarea
                value={text}
                onChange={(e) => setText(e.target.value)}
                placeholder="Paste your news article, headline, or any text you want to verify..."
                className="w-full h-52 p-5 border-2 border-gray-200 rounded-2xl focus:border-indigo-400 focus:ring-4 focus:ring-indigo-100 focus:outline-none resize-none text-gray-700 transition-all shadow-inner bg-gradient-to-br from-white to-indigo-50/20 font-medium"
              />
              <div className="absolute bottom-4 right-4 bg-white/90 backdrop-blur-sm px-3 py-1 rounded-full shadow-lg">
                <span className={`text-sm font-semibold ${text.length >= 10 ? 'text-green-600' : 'text-gray-400'}`}>
                  {text.length} chars
                </span>
              </div>
            </div>
            {text.length > 0 && (
              <button
                onClick={clearAll}
                className="mt-3 text-sm text-red-500 hover:text-red-700 font-bold flex items-center gap-1 transition-colors"
              >
                <span>âœ•</span> Clear Text
              </button>
            )}
          </div>

          {/* Action Button with Glow Effect */}
          <button
            onClick={analyzeNews}
            disabled={loading || text.length < 10}
            className={`w-full py-5 rounded-2xl font-bold text-white text-lg transition-all transform hover:scale-[1.02] active:scale-[0.98] shadow-xl relative overflow-hidden ${
              loading || text.length < 10
                ? 'bg-gray-400 cursor-not-allowed'
                : 'bg-gradient-to-r from-indigo-600 via-purple-600 to-blue-600 hover:shadow-2xl'
            }`}
          >
            {!loading && !result && (
              <div className="absolute inset-0 bg-gradient-to-r from-transparent via-white to-transparent opacity-20 animate-shimmer"></div>
            )}
            {loading ? (
              <span className="flex items-center justify-center gap-3">
                <Loader className="w-6 h-6 animate-spin" />
                Analyzing with AI...
              </span>
            ) : (
              <span className="flex items-center justify-center gap-2">
                <Sparkles className="w-5 h-5" />
                Analyze News Article
                <Sparkles className="w-5 h-5" />
              </span>
            )}
          </button>

          {/* Error Message */}
          {error && (
            <div className="mt-6 p-5 bg-gradient-to-r from-red-50 to-pink-50 border-2 border-red-300 rounded-2xl flex items-start gap-3 animate-shake shadow-lg">
              <AlertCircle className="w-6 h-6 text-red-500 mt-0.5 flex-shrink-0" />
              <div>
                <p className="text-red-900 font-bold text-lg">Error Detected</p>
                <p className="text-red-700 text-sm mt-1">{error}</p>
              </div>
            </div>
          )}

          {/* Results with Animation */}
          {result && (
            <div className="mt-8 space-y-5 animate-slide-up">
              <div
                className={`p-8 rounded-3xl border-2 shadow-2xl relative overflow-hidden ${
                  result.prediction === 'REAL'
                    ? 'bg-gradient-to-br from-green-50 via-emerald-50 to-teal-50 border-green-300'
                    : 'bg-gradient-to-br from-red-50 via-orange-50 to-pink-50 border-red-300'
                }`}
              >
                {/* Animated Background Pattern */}
                <div className="absolute inset-0 opacity-5">
                  <div className="absolute inset-0" style={{
                    backgroundImage: 'radial-gradient(circle, currentColor 1px, transparent 1px)',
                    backgroundSize: '20px 20px'
                  }}></div>
                </div>

                <div className="relative z-10">
                  <div className="flex items-center gap-4 mb-6">
                    <div className={`p-4 rounded-2xl ${
                      result.prediction === 'REAL' 
                        ? 'bg-green-200 shadow-lg shadow-green-300/50' 
                        : 'bg-red-200 shadow-lg shadow-red-300/50'
                    }`}>
                      {result.prediction === 'REAL' ? (
                        <CheckCircle className="w-10 h-10 text-green-700" />
                      ) : (
                        <AlertTriangle className="w-10 h-10 text-red-700" />
                      )}
                    </div>
                    <div>
                      <h3 className="text-4xl font-black text-gray-900 tracking-tight">
                        {result.prediction === 'REAL' ? 'âœ“ REAL NEWS' : 'âš  FAKE NEWS'}
                      </h3>
                      <p className={`text-sm font-bold mt-1 ${
                        result.prediction === 'REAL' ? 'text-green-700' : 'text-red-700'
                      }`}>
                        AI Verification Complete
                      </p>
                    </div>
                  </div>

                  {/* Enhanced Confidence Bars */}
                  <div className="space-y-4 mb-6">
                    <div className="bg-white/70 backdrop-blur-sm p-5 rounded-2xl shadow-lg">
                      <div className="flex justify-between mb-2">
                        <span className="text-sm font-bold text-gray-800 flex items-center gap-2">
                          <CheckCircle className="w-4 h-4 text-green-600" />
                          Real News Probability
                        </span>
                        <span className="text-lg font-black text-green-700">
                          {result.confidence_percentage.real}
                        </span>
                      </div>
                      <div className="w-full bg-gray-200 rounded-full h-5 shadow-inner">
                        <div
                          className="bg-gradient-to-r from-green-400 via-green-500 to-green-600 h-5 rounded-full transition-all duration-1000 shadow-lg relative overflow-hidden"
                          style={{ width: result.confidence_percentage.real }}
                        >
                          <div className="absolute inset-0 bg-gradient-to-r from-transparent via-white to-transparent opacity-30 animate-shimmer"></div>
                        </div>
                      </div>
                    </div>

                    <div className="bg-white/70 backdrop-blur-sm p-5 rounded-2xl shadow-lg">
                      <div className="flex justify-between mb-2">
                        <span className="text-sm font-bold text-gray-800 flex items-center gap-2">
                          <AlertTriangle className="w-4 h-4 text-red-600" />
                          Fake News Probability
                        </span>
                        <span className="text-lg font-black text-red-700">
                          {result.confidence_percentage.fake}
                        </span>
                      </div>
                      <div className="w-full bg-gray-200 rounded-full h-5 shadow-inner">
                        <div
                          className="bg-gradient-to-r from-red-400 via-red-500 to-red-600 h-5 rounded-full transition-all duration-1000 shadow-lg relative overflow-hidden"
                          style={{ width: result.confidence_percentage.fake }}
                        >
                          <div className="absolute inset-0 bg-gradient-to-r from-transparent via-white to-transparent opacity-30 animate-shimmer"></div>
                        </div>
                      </div>
                    </div>
                  </div>

                  <div className="bg-gradient-to-r from-indigo-50 to-purple-50 border-2 border-indigo-200 rounded-2xl p-5 shadow-lg">
                    <div className="flex items-start gap-3">
                      <Sparkles className="w-6 h-6 text-indigo-600 mt-0.5 flex-shrink-0" />
                      <div>
                        <p className="text-sm font-bold text-indigo-900 mb-1">Important Reminder</p>
                        <p className="text-sm text-indigo-800">
                          This is an AI-powered prediction with 98.79% accuracy. 
                          Always cross-verify important news from multiple trusted sources.
                        </p>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>

        {/* Sample Texts with Cards */}
        <div className="bg-white/80 backdrop-blur-md rounded-3xl shadow-2xl p-8 border border-white/50">
          <h3 className="text-2xl font-bold text-gray-800 mb-6 flex items-center gap-3">
            <Sparkles className="w-6 h-6 text-purple-600" />
            Try Sample Articles
          </h3>
          <div className="grid md:grid-cols-3 gap-4">
            {samples.map((sample, index) => (
              <button
                key={index}
                onClick={() => loadSample(sample.text)}
                className={`text-left p-6 rounded-2xl border-2 transition-all transform hover:scale-105 hover:shadow-xl active:scale-95 group ${
                  sample.type === 'real'
                    ? 'bg-gradient-to-br from-green-50 to-emerald-50 border-green-200 hover:border-green-300'
                    : 'bg-gradient-to-br from-red-50 to-rose-50 border-red-200 hover:border-red-300'
                }`}
              >
                <div className="text-3xl mb-3 group-hover:scale-110 transition-transform">{sample.icon}</div>
                <p className="text-sm text-gray-700 font-medium leading-relaxed">{sample.text}</p>
              </button>
            ))}
          </div>
        </div>

        {/* Footer */}
        <div className="text-center mt-10 space-y-2">
          <div className="flex items-center justify-center gap-2 text-gray-600 font-medium">
            <div className="w-2 h-2 bg-indigo-500 rounded-full animate-pulse"></div>
