import React, { useState } from "react";
import {
  Loader,
  ShieldCheck,
  AlertTriangle,
  FileText,
  Sparkles,
  CheckCircle,
} from "lucide-react";

export default function FakeNewsDetector() {
  const [text, setText] = useState("");
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const API_URL = "http://localhost:5000/predict";

  const analyzeNews = async () => {
    if (text.length < 10) {
      setError("Please enter at least 10 characters.");
      return;
    }

    setLoading(true);
    setError("");
    setResult(null);

    try {
      const response = await fetch(API_URL, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ text }),
      });

      const data = await response.json();
      if (response.ok) setResult(data);
      else setError(data.error || "Failed to analyze text.");
    } catch (err) {
      setError("Cannot connect to the server. Is Flask running?");
    } finally {
      setLoading(false);
    }
  };

  const samples = [
    { text: "MIT scientists discovered a new renewable energy source.", icon: "ðŸ”¬" },
    { text: "You wonâ€™t believe what this celebrity did!", icon: "âš ï¸" },
    { text: "Government announces new budget updates.", icon: "ðŸ›ï¸" },
  ];

  return (
    <div className="min-h-screen bg-gradient-to-b from-indigo-100 via-purple-50 to-white p-6">

      {/* HERO SECTION */}
      <div className="max-w-4xl mx-auto text-center mb-14">
        <div className="inline-flex items-center justify-center p-4 bg-white/70 backdrop-blur-xl border border-indigo-200/50 rounded-2xl shadow-md mb-6">
          <ShieldCheck className="w-12 h-12 text-indigo-600" />
        </div>

        <h1 className="text-5xl font-extrabold text-gray-900 tracking-tight">
          Fake News Detector
        </h1>

        <p className="text-gray-600 mt-3 text-lg flex justify-center items-center gap-2">
          <Sparkles className="w-5 h-5 text-purple-500" />
          Smart AI Verification Tool
          <Sparkles className="w-5 h-5 text-purple-500" />
        </p>
      </div>

      {/* MAIN CARD */}
      <div className="max-w-4xl mx-auto bg-white/80 backdrop-blur-xl border border-gray-200 rounded-2xl shadow-xl p-8">

        {/* TEXT INPUT */}
        <label className="block mb-2 text-sm font-bold text-gray-700 flex items-center gap-2">
          <FileText className="w-4 h-4 text-indigo-500" /> Enter Text to Analyze
        </label>

        <textarea
          value={text}
          onChange={(e) => setText(e.target.value)}
          placeholder="Paste a news article or headline..."
          className="w-full h-44 p-4 rounded-xl border border-gray-300 bg-white shadow-inner focus:ring-2 focus:ring-indigo-300 outline-none"
        />

        {/* BUTTON */}
        <button
          onClick={analyzeNews}
          disabled={loading || text.length < 10}
          className={`mt-5 w-full py-4 rounded-xl font-bold text-white text-lg shadow-md transition ${
            text.length < 10
              ? "bg-gray-300 cursor-not-allowed"
              : "bg-gradient-to-r from-indigo-600 to-purple-600 hover:brightness-110"
          }`}
        >
          {loading ? (
            <span className="flex items-center justify-center gap-3">
              <Loader className="w-6 h-6 animate-spin" />
              Analyzing...
            </span>
          ) : (
            "Analyze"
          )}
        </button>

        {/* ERROR */}
        {error && (
          <div className="mt-5 p-4 bg-red-50 border border-red-300 rounded-xl text-red-700 flex gap-3 items-center">
            <AlertTriangle className="w-5 h-5" />
            {error}
          </div>
        )}

        {/* RESULT */}
        {result && (
          <div
            className={`mt-8 p-6 rounded-2xl border shadow-md ${
              result.prediction === "REAL"
                ? "bg-green-50 border-green-300"
                : "bg-red-50 border-red-300"
            }`}
          >
            <div className="flex items-center gap-4 mb-3">
              {result.prediction === "REAL" ? (
                <CheckCircle className="w-10 h-10 text-green-600" />
              ) : (
                <AlertTriangle className="w-10 h-10 text-red-600" />
              )}
              <h2 className="text-3xl font-bold text-gray-900">
                {result.prediction === "REAL" ? "Real News" : "Fake News"}
              </h2>
            </div>

            <p className="text-gray-700 font-medium mb-1">
              Real Probability: {result.confidence_percentage.real}
            </p>

            <p className="text-gray-700 font-medium">
              Fake Probability: {result.confidence_percentage.fake}
            </p>
          </div>
        )}
      </div>

      {/* SAMPLE SECTION */}
      <div className="max-w-4xl mx-auto mt-14">
        <h2 className="text-2xl font-bold text-gray-800 mb-4">Try a sample</h2>

        <div className="grid md:grid-cols-3 gap-4">
          {samples.map((s, i) => (
            <button
              key={i}
              onClick={() => setText(s.text)}
              className="p-5 bg-white/80 backdrop-blur-xl border rounded-2xl shadow-md hover:shadow-xl transition text-left"
            >
              <div className="text-3xl mb-2">{s.icon}</div>
              <p className="text-gray-700">{s.text}</p>
            </button>
          ))}
        </div>
      </div>
    </div>
  );
}
